mapper.update:
update t_order


        order_status = #{orderStatus}


        pay_mode = #{payMode}


where order_id = #{orderId}

mapper.select:
select order_id as orderNo,receive_name as receiveName,receive_phone as receivePhone,order_status as orderStatus, pay_mode as payMode,total_price as totalPrice,preferences_price as preferencesPrice,integral_price as integralPrice, delivery_fee as deliveryFee,create_time as createTime,es_arrive_time as esArriveTime from t_order where dr=0
and DATE_FORMAT(create_time, '%Y-%m-%d %H:%i:%s') >= DATE_FORMAT(#{beginTime}, '%Y-%m-%d %H:%i:%s')
and DATE_FORMAT(create_time, '%Y-%m-%d %H:%i:%s') <= DATE_FORMAT(#{endTime}, '%Y-%m-%d %H:%i:%s')
and order_id =#{orderNo}
and order_status =#{orderState}
order by create_time desc
